        Quit $$$OK

 i id="" q "Не задан ID заявки."
 s obj=##class(Docs.LoanApp).%OpenId(id)
 i '$IsObject(obj) q "Не найдена заявки с ID="_id_"."
 set obj=##class(System.TInfoObject).%New()
 set res=##class(System.Options).GetOptions(obj,$$$ApplicationSettingsFirmName,.lOrganisation)
 s Parameters("Value","FirmName")=lOrganisation

 s loanapp=##class(Docs.LoanApp).%OpenId(id)
 If '$IsObject(loanapp) {s ErrText="Не найдена заявка ID="_id $$$SysLogError($$$EMCSysErr,ErrText) d OK^%WMUTIL(ErrText) }
 Else 
 {
   s Parameters("Value","ID")			=id
   s Parameters("Value","loanapp")		=loanapp
   s Parameters("Value","laSysCode")		=loanapp.SysCode
   i $IsObject(loanapp.laClientID) {
	   s Parameters("Value","laFirstName")		=loanapp.laClientID.PersonID.FirstName
	   s Parameters("Value","laLastName")		=loanapp.laClientID.PersonID.LastName
	   s Parameters("Value","laMiddleName")		=loanapp.laClientID.PersonID.MiddleName
	   s Parameters("Value","laBirthDay")		=$$$HorologToDateEdit(loanapp.laClientID.PersonID.DateOfBirth)
	   s Parameters("Value","laAge")		=$h-loanapp.laClientID.PersonID.DateOfBirth\365
   }
   i $IsObject(loanapp.laPlaceOfBirth) {
	   s Parameters("Value","laPlaceOfBirth")=loanapp.laPlaceOfBirth.GetAddressForLoanAppInfo()
   }
   s Parameters("Value","laResident")		="РФ"
   s Parameters("Value","laGender")		=loanapp.laSexLogicalToDisplay(loanapp.laSex)
   i $IsObject(loanapp.laLoanAppAddID) {
	   s Parameters("Value","laReservist")=loanapp.laLoanAppAddID.laaReservistLogicalToDisplay(loanapp.laLoanAppAddID.laaReservist)
       s Parameters("Value","laConviction")=loanapp.laLoanAppAddID.laaConvictionLogicalToDisplay(loanapp.laLoanAppAddID.laaConviction)
       s Parameters("Value","laQIWIPay")=loanapp.laLoanAppAddID.laaQIWIPayLogicalToDisplay(loanapp.laLoanAppAddID.laaQIWIPay)
  	   s Parameters("Value","laEducation")=loanapp.laLoanAppAddID.laaEducationLogicalToDisplay(loanapp.laLoanAppAddID.laaEducation)
       s Parameters("Value","laEmployment")=loanapp.laLoanAppAddID.laaEmploymentLogicalToDisplay(loanapp.laLoanAppAddID.laaEmployment)
       s Parameters("Value","laEnterprise")=loanapp.laLoanAppAddID.laaEnterpriseLogicalToDisplay(loanapp.laLoanAppAddID.laaEnterprise)
       s DCMinPeriod=loanapp.laLoanAppAddID.laaLastStage
       s Parameters("Value","laLastStage1")=DCMinPeriod
       i (loanapp.laLoanAppAddID.laaLastStageEdIzm'=1) {
       	s EdCode=loanapp.laLoanAppAddID.laaLastStageEdIzm
       	i EdCode=2 {s EdCode=4 } elseIf EdCode=4 {s EdCode=1}
		if DCMinPeriod {
		      s DCMinPeriodPrp=##class(System.TUtil).NumberToString(DCMinPeriod,$s(EdCode=2:2,1:1),2)
		      s Ed1=##class(BL.Docs.GP).PeriodInUnit(DCMinPeriod,0,EdCode,2)
	       	s Parameters("Value","laLastStage")=loanapp.laLoanAppAddID.laaLastStage_" "_Ed1
	      } else {
	       	s Parameters("Value","laLastStage")=loanapp.laLoanAppAddID.laaLastStage_" ("_loanapp.laLoanAppAddID.laaLastStageEdIzmLogicalToDisplay(loanapp.laLoanAppAddID.laaLastStageEdIzm)
	      }
      } else {
	      s Parameters("Value","laLastStage")=loanapp.laLoanAppAddID.laaLastStageEdIzmLogicalToDisplay(loanapp.laLoanAppAddID.laaLastStageEdIzm)
      }
   }
   i $IsObject(loanapp.laPhoneMobileID) {
	   s Parameters("Value","laContactPhone")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laPhoneMobileID.phNumber)
   }
   s Parameters("Value","laEmail")=loanapp.laEMail
   i $IsObject(loanapp.laPassportID) {
	   s Parameters("Value","laPassportSeries")=loanapp.laPassportID.psSeries
       s Parameters("Value","laPassportNumber")=loanapp.laPassportID.psNumber
       s Parameters("Value","laPassportIssue")=loanapp.laPassportID.psIssueDep
       s Parameters("Value","laPassportDateIssue")=$s(loanapp.laPassportID.psDateIssue>0:$$$HorologToDateEdit(loanapp.laPassportID.psDateIssue),1:"")
       s Parameters("Value","laPassportDepCode")=loanapp.laPassportID.psIssueDepCode
   }
   s IsDriversLicense=$IsObject(loanapp.laLoanAppAddID.laaDriversLicenseID)
   if IsDriversLicense {
   	s Parameters("Value","laIsDriversLicense")="Да"
   	s Parameters("Value","laDriversLicenseSeries")=loanapp.laLoanAppAddID.laaDriversLicenseID.dlSeries
   	s Parameters("Value","laDriversLicenseNumber")=loanapp.laLoanAppAddID.laaDriversLicenseID.dlNumber
   	s Parameters("Value","laDriversLicenseIssue")=loanapp.laLoanAppAddID.laaDriversLicenseID.dlIssueDep
   	s Parameters("Value","laDriversLicenseDateIssue")=$s(loanapp.laLoanAppAddID.laaDriversLicenseID.dlDateIssue>0:$$$HorologToDateEdit(loanapp.laLoanAppAddID.laaDriversLicenseID.dlDateIssue),1:"")
   }
   else {s Parameters("Value","laIsDriversLicense")="Нет"}
   s IsForeignPassport=($IsObject(loanapp.laLoanAppAddID.laaForeignPassportID) && (loanapp.laLoanAppAddID.laaForeignPassportID.fpNumber'="") && (loanapp.laLoanAppAddID.laaForeignPassportID.fpDateIssue>0))
   if IsForeignPassport {
   	s Parameters("Value","laIsForeignPassport")="Да"
   	s Parameters("Value","laForeignPassportSeries")=loanapp.laLoanAppAddID.laaForeignPassportID.fpSeries
   	s Parameters("Value","laForeignPassportNumber")=loanapp.laLoanAppAddID.laaForeignPassportID.fpNumber
   	s Parameters("Value","laForeignPassportIssue")=loanapp.laLoanAppAddID.laaForeignPassportID.fpIssueDep
   	s Parameters("Value","laForeignPassportDateIssue")=$s(loanapp.laLoanAppAddID.laaForeignPassportID.fpDateIssue>0:$$$HorologToDateEdit(loanapp.laLoanAppAddID.laaForeignPassportID.fpDateIssue),1:"")
   }
   else {s Parameters("Value","laIsForeignPassport")="Нет"} 
   s IsMilitaryPassport=$IsObject(loanapp.laLoanAppAddID.laaMilitaryPassportID)
   if IsMilitaryPassport {
   	s Parameters("Value","laIsMilitaryPassport")="Да"
   	s Parameters("Value","laMilitaryPassportSeries")=loanapp.laLoanAppAddID.laaMilitaryPassportID.mpSeries
   	s Parameters("Value","laMilitaryPassportNumber")=loanapp.laLoanAppAddID.laaMilitaryPassportID.mpNumber
   	s Parameters("Value","laMilitaryPassportIssue")=loanapp.laLoanAppAddID.laaMilitaryPassportID.mpIssueDep
   	s Parameters("Value","laMilitaryPassportDateIssue")=$s(loanapp.laLoanAppAddID.laaMilitaryPassportID.mpDateIssue>0:$$$HorologToDateEdit(loanapp.laLoanAppAddID.laaMilitaryPassportID.mpDateIssue),1:"")
   }
   else {s Parameters("Value","laIsMilitaryPassport")="Нет"}  
   // 24.08.2011 Червяков 
   // Выводим то, что ввели
    //s IsINN=($IsObject(loanapp.laLoanAppAddID.laaINNID) && (loanapp.laLoanAppAddID.laaINNID.innDate>0))
   s IsINN=($IsObject(loanapp.laLoanAppAddID.laaINNID) && (loanapp.laLoanAppAddID.laaINNID.innNumber'="            "))
   if IsINN {
   	s Parameters("Value","laIsINN")="Да"
   	s Parameters("Value","laINNNumber")=loanapp.laLoanAppAddID.laaINNID.innNumber
   }
   else {s Parameters("Value","laIsINN")="Нет"}     
   // 24.08.2011 Червяков 
   // Выводим то, что ввели
   // s IsPFR=($IsObject(loanapp.laLoanAppAddID.laaPFRID) && (loanapp.laLoanAppAddID.laaPFRID.pfrDate>0))
   s IsPFR=($IsObject(loanapp.laLoanAppAddID.laaPFRID) && (loanapp.laLoanAppAddID.laaPFRID.pfrNumber'="   -   -      "))
   if IsPFR {
   	s Parameters("Value","laIsPFR")="Да"
   	s Parameters("Value","laPFRNumber")=loanapp.laLoanAppAddID.laaPFRID.pfrNumber
   }
   else {s Parameters("Value","laIsPFR")="Нет"}     
   i $IsObject(loanapp.laRegAddrID) {
	   s Parameters("Value","laRegArea")=loanapp.laRegAddrID.AREA
   	   s Parameters("Value","laRegRegion")=loanapp.laRegAddrID.REGION
   	   s Parameters("Value","laRegCity")=loanapp.laRegAddrID.CITY_loanapp.laRegAddrID.PLACE
   	   s Parameters("Value","laRegStreet")=loanapp.laRegAddrID.STREET
   	   s Parameters("Value","laRegHouse")=loanapp.laRegAddrID.HOUSE
   	   s Parameters("Value","laRegBuilding")=loanapp.laRegAddrID.BUILDING
   	   s Parameters("Value","laRegApartment")=loanapp.laRegAddrID.APARTMENT
   }

   s Parameters("Value","laRegBuilding2")=""
   i $IsObject(loanapp.laPhoneRegID) {
	   s Parameters("Value","laRegPhone")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laPhoneRegID.phNumber)
   }
   i $IsObject(loanapp.laLiveAddrID) {
	   s Parameters("Value","laFactArea")=loanapp.laLiveAddrID.AREA
	   s Parameters("Value","laFactRegion")=loanapp.laLiveAddrID.REGION
	   s Parameters("Value","laFactCity")=loanapp.laLiveAddrID.CITY_loanapp.laLiveAddrID.PLACE
	   s Parameters("Value","laFactStreet")=loanapp.laLiveAddrID.STREET
	   s Parameters("Value","laFactHouse")=loanapp.laLiveAddrID.HOUSE
	   s Parameters("Value","laFactBuilding")=loanapp.laLiveAddrID.BUILDING
	   s Parameters("Value","laFactApartment")=loanapp.laLiveAddrID.APARTMENT
   }
   s Parameters("Value","laFactBuilding2")=""
   i $IsObject(loanapp.laPhoneLiveID) {
	   s Parameters("Value","laFactPhone")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laPhoneLiveID.phNumber)
   }
   s Parameters("Value","laIsFactRegAddress")=$s($IsObject(loanapp.laRegAddrID) && $IsObject(loanapp.laLiveAddrID) &&(loanapp.laRegAddrID.%Id()=loanapp.laLiveAddrID.%Id()):"Да",1:"Нет") 
   s Parameters("Value","laIsMarried")=loanapp.laMaritalStatusLogicalToDisplay(loanapp.laMaritalStatus)
   i $IsObject(loanapp.laMarriedPersonID) {
	   s Parameters("Value","laMarritalFIO")=loanapp.laMarriedPersonID.FullName
	   s Parameters("Value","laMarrtalBirthDay")=$s(loanapp.laMarriedPersonID.DateOfBirth>0:$$$HorologToDateEdit(loanapp.laMarriedPersonID.DateOfBirth),1:"")
   }
   s Parameters("Value","laMarrtalEmployment")=loanapp.laMarriedEmploymentLogicalToDisplay(loanapp.laMarriedEmployment)
   s Parameters("Value","laMarriedPersonPhone")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laMarriedPersonPhone.phNumber)
   s Parameters("Value","laChildrenCount")=$s(loanapp.laChildrenCount>0:loanapp.laChildrenCount,1:"")
   s Parameters("Value","laNoChildrenCount")=$s(loanapp.laNoChildrenCount>0:loanapp.laNoChildrenCount,1:"")
   s Parameters("Value","laOtherCount")=$s(loanapp.laOtherCount>0:loanapp.laOtherCount,1:"")

   s Parameters("Value","laWorkArea")=loanapp.laWorkArea
   s Parameters("Value","laParennameJob")=loanapp.laParentNameJob
   s Parameters("Value","laJobAddress")=$s($IsObject(loanapp.laJobAddressID):loanapp.laJobAddressID.GetAddressForLoanAppInfo(),1:"")

   i $IsObject(loanapp.laJobPhoneCityID) {
	   s Parameters("Value","laJobPhoneCity")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laJobPhoneCityID.phNumber)
   }
   i $IsObject(loanapp.laJobParentPhoneCityID) {
	   s Parameters("Value","laJobParentPhoneCity")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laJobParentPhoneCityID.phNumber)
   }
   s Parameters("Value","laContactPersonFIO")=loanapp.laLastNameContactPerson_" "_loanapp.laFirstNameContactPerson_" "_loanapp.laMiddleNameContactPerson
   s Parameters("Value","laContactPersonBirthDay")=$s(loanapp.laContactPersonBirthday>0:$$$HorologToDateEdit(loanapp.laContactPersonBirthday),1:"")
   s Parameters("Value","laContactPersonStatus")=loanapp.laContactPersonStatusLogicalToDisplay(loanapp.laContactPersonStatus)
   s Parameters("Value","laContactPersonPhone")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laContactPersonPhoneID.phNumber)
   s Parameters("Value","laContactPersonMobilePhone")=##class(BL.Common.Phone).SetFormatPhone(loanapp.laContactPersonMobilePhone)

   s Parameters("Value","laVehicle")=loanapp.laLoanAppAddID.laaVehicleBrand_" "_loanapp.laLoanAppAddID.laaVehicleModel_" "_loanapp.laLoanAppAddID.laaVehicleNumber_" "_loanapp.laLoanAppAddID.laaVehicleYear

   if $isObject(loanapp.laLoanAppAddID.laaRealtyCollectID) {
	    s card=loanapp.laLoanAppAddID.laaRealtyCollectID
		s lb=$lb("Квартира/доля","Дом","Дача","Гараж","Земельный участок","Другое")
		for ii="01","02","03","04","05","06" {
			 	s propname="cbRealty"_ii
 				s str=$s($zObjProperty(card,"reaP"_ii)=1:$lg(lb,+ii),1:"")
 				if str'="" {s Parameters("Value","laRealty")=$g(%W("Obj").Data("laRealty"))_" "_str}

		}   
   }
   else  {
	   s Parameters("Value","laRealty")=""
   }
   //s Parameters("Value","laRealty")=$tr(%W("Obj").Data("laRealty")," ",",")	   
   s res=##class(BL.Docs.LoanApp).SecondLoanAppCheck(loanapp.laClientID.PersonID)
   if res {   s Parameters("Value","laRepeat")="Повторный"}
   else {s Parameters("Value","laRepeat")="Первичный"}
   s Parameters("Value","laPeriod")=loanapp.laPeriod_" "_$s(loanapp.laProductTypeID.PTEd=1:"дн.",loanapp.laProductTypeID.PTEd=2:"нед.",loanapp.laProductTypeID.PTEd=3:"мес.",loanapp.laProductTypeID.PTEd=4:"г.",loanapp.laProductTypeID.PTEd=$$$ptedOther:"Периоды по "_loanapp.laProductTypeID.PTDaysNumber_" дн.",1:"")
   s Parameters("Value","laSumm")=loanapp.laSum
   s Parameters("Value","laProductType")=loanapp.laProductTypeID.ObjectName
   s Parameters("Value","laObjective")=loanapp.laLoanAppAddID.laaObjectiveLogicalToDisplay(loanapp.laLoanAppAddID.laaObjective)

   s Parameters("Value","laIncome")=loanapp.laIncome_$s(loanapp.laIncome>0:" руб.",1:"")
   s Parameters("Value","laAmountSpending")=loanapp.laAmountSpending_$s(loanapp.laAmountSpending>0:" руб.",1:"")

    if $isObject(loanapp.laLoanAppAddID.laaIncomeCollectID) {
	    s card=loanapp.laLoanAppAddID.laaIncomeCollectID
		s lb=$lb("Основная зарплата, премии","Предпринимательство","Работа по совместительству","Пенсия и пособия","Сдача в аренду","Проценты по вкладам","Дивиденты","Субсидии","Стипендия","Прочие")
		for ii="01","02","03","04","05","06","07","08","09","10" {
 			s propname="cbIncome"_ii
 			s str=$s($zObjProperty(card,"incP"_ii)=1:$lg(lb,+ii),1:"")
 			if str'="" {s Parameters("Value","laIncomeSource")=$g(%W("Obj").Data("laIncomeSource"))_" "_str}

		}   
   }
   else  {
	   s Parameters("Value","laIncomeSource")=""
   }
   if $isObject(loanapp.laLoanAppAddID.laaExpenseCollectID) {
	    s card=loanapp.laLoanAppAddID.laaExpenseCollectID
		s lb=$lb("Текущие расходы","Комм.платежи","Алименты","Обслуживание автомобиля","Расходы на образование","Прочие")
		for ii="01","02","03","04","05","06" {
 			;s propname="cbIncome"_ii
 			s str=$s($zObjProperty(card,"expP"_ii)=1:$lg(lb,+ii),1:"")
 			if str'="" {s Parameters("Value","laaExpenseCollect")=$g(%W("Obj").Data("laaExpenseCollect"))_" "_str}

		}   
   }
   else  {
	   s Parameters("Value","laaExpenseCollect")=""
   }
   s Parameters("Value","laBorrowed")=loanapp.laLoanAppAddID.laaBorrowedLogicalToDisplay(loanapp.laLoanAppAddID.laaBorrowed)
   s Parameters("Value","laHaveBorrowed")=loanapp.laLoanAppAddID.laaHaveBorrowedLogicalToDisplay(loanapp.laLoanAppAddID.laaHaveBorrowed)
   i $IsObject(loanapp.laLoanAppAddID.laaCreditOrganization) {
   	s Parameters("Value","laBorrowCompany")=loanapp.laLoanAppAddID.laaCreditOrganization.NAME
   } else {s Parameters("Value","laBorrowCompany")=""}
   s Parameters("Value","laBorrowType")=loanapp.laLoanAppAddID.laaBorrowType
   s Parameters("Value","laBorrowSumm")=loanapp.laLoanAppAddID.laaBorrowSumm_$s(loanapp.laLoanAppAddID.laaBorrowSumm>0:" руб.",1:"")
   s Parameters("Value","laBorrowDebt")=loanapp.laLoanAppAddID.laaBorrowDebt_$s(loanapp.laLoanAppAddID.laaBorrowDebt>0:" руб.",1:"")
   s Parameters("Value","laBorrowPay")=loanapp.laLoanAppAddID.laaBorrowPay_$s(loanapp.laLoanAppAddID.laaBorrowPay>0:" руб.",1:"")

   s Parameters("Value","laLoanAppSource")=loanapp.laLoanAppSourceID.ObjectName
   s Parameters("Value","laDateReg")			=$$D^%FD(loanapp.laDateReg,"""d"" $m Y г.")	

}
 set Parameters("Value","CurrentDate")=$$D^%FD(+$h,""" d ""  $m  Yг.")
  s user=##class(System.TAccount).GetSessionAccount()
 set Parameters("Value","UserName")=user.ShortName
 	q ""
